const http = require('http');

http.createServer(function (req, res) {
    let content = '';
    res.writeHead(200, {
        "Content-Type": "text/plain",
        "Access-Control-Allow-Origin": "*"
    });
    req.on('data', function (chunk) {
        content += chunk;
    });
    req.on('end', function () {
        res.write(content);
        console.log(content);
        res.end();
    })
}).listen(8080);